mapper.select:
SELECT clno, cltitle, cllevel, aid FROM ( SELECT rownum n, c.clno, c.cltitle, c.cllevel, c.aid FROM class c JOIN confirm cf ON c.clno = cf.clno WHERE cf.cfcd = '대기중'
AND ( cf.clno = #{keyword, jdbcType=NUMERIC} OR cltitle LIKE '%' || #{keyword, jdbcType=VARCHAR} || '%' OR cllevel LIKE '%' || #{keyword, jdbcType=VARCHAR} || '%' OR aid LIKE '%' || #{keyword, jdbcType=VARCHAR} || '%' )
AND cf.clno = #{keyword, jdbcType=NUMERIC}
AND ${searchColumn} LIKE '%' || #{keyword, jdbcType=VARCHAR} || '%'
ORDER BY cf.clno ASC ) a WHERE a.n BETWEEN #{start} AND #{end}
